function getRecommendedPathways() {
    const recommended = [];
    
    skillsData.forEach((skill, index) => {
        if (skill.completed && PATHWAYS_DATA[index]) {
            const pathways = PATHWAYS_DATA[index];
            pathways.forEach(pathway => {
                // Check if pathway is already completed
                if (completedPathways.includes(pathway.id)) {
                    return;
                }
                
                // Check if score is below threshold (pathway is relevant)
                if (skill.score >= pathway.scoreThreshold) {
                    return;
                }
                
                // Check prerequisites
                if (pathway.prerequisites && pathway.prerequisites.length > 0) {
                    const prereqsMet = pathway.prerequisites.every(prereqId => 
                        completedPathways.includes(prereqId)
                    );
                    if (!prereqsMet) {
                        return; // Skip if prerequisites not met
                    }
                }
                
                recommended.push({
                    ...pathway,
                    skillIndex: index,
                    skillName: skill.name,
                    skillSymbol: skill.symbol,
                    currentScore: skill.score
                });
            });
        }
    });
    
    // Sort by priority (lower = more important), then by score difference
    recommended.sort((a, b) => {
        // First sort by priority
        const priorityA = a.priority || 99;
        const priorityB = b.priority || 99;
        if (priorityA !== priorityB) {
            return priorityA - priorityB;
        }
        // Then by how far below threshold (bigger gap = more urgent)
        const gapA = a.scoreThreshold - a.currentScore;
        const gapB = b.scoreThreshold - b.currentScore;
        return gapB - gapA;
    });
    
    return recommended;
}

    function getPathwayProgress(pathwayId) {
        if (!pathwayProgress[pathwayId]) {
            pathwayProgress[pathwayId] = {};
        }
        return pathwayProgress[pathwayId];
    }

    function isPathwayComplete(pathway) {
        const progress = getPathwayProgress(pathway.id);
        return pathway.tasks.every(task => progress[task.id] === true);
    }

    function countCompletedTasks(pathway) {
        const progress = getPathwayProgress(pathway.id);
        return pathway.tasks.filter(task => progress[task.id] === true).length;
    }

